import React from 'react'
import {connect} from 'react-redux'
import {Link, withRouter} from 'react-router'
import {compose} from 'redux'
import R from 'ramda'
import classNames from 'classnames'

import {
  getTypes,
  getActiveTypeId
} from 'selectors'

const Types = ({types, activeTypeId}) => {
  const renderType = (type, index) => {
    const getActiveState = R.propEq('id', activeTypeId)

    const linkClass = classNames({
      'list-group-item': true,
      'active': getActiveState(type)
    })

    return (
      <Link
        to={`/types/${type.id}`}
        className={linkClass}
        key={index}
      >
        {type.name}
      </Link>
    )
  }

  const renderAllType = () => {
    const linkClass = classNames({
      'list-group-item': true,
      'active': R.isNil(activeTypeId)
    })

    return (
      <Link
        to='/'
        className={linkClass}
      >
        All
      </Link>
    )
  }

  return (
    <div className='well'>
      <h4>Types</h4>
      <div className='list-group'>
        {renderAllType()}
        {types.map((type, index) => renderType(type, index))}
      </div>
    </div>
  )
}

const mapStateToProps = (state, ownProps) => ({
    types: getTypes(state),
    activeTypeId: getActiveTypeId(ownProps)
})

export default compose(
  withRouter,
  connect(mapStateToProps, null)
)(Types)
